import { 
    ScrollPage, 
    Animator,
    batch,
    Fade,
    FadeIn, 
    MoveOut,
    Sticky,
    StickyIn,
    ZoomIn
} from 'react-scroll-motion'
import img0 from '../../img/0.jpeg'
import img1 from '../../img/1.jpeg'
import img2 from '../../img/2.jpeg'
import img3 from '../../img/3.png'


import './index.css'

const work = [
    {
        name: 'Lukuvinkki',
        link: 'https://github.com/hhautajarvi/Ohtuminiprojekti',
        imageSrc: img0
    },
    {
        name: 'Silly Advice',
        link: 'https://github.com/lina-ova/advice-app-react',
        imageSrc: img1
    },
    {
        name: 'Posting Platform (yet under work)',
        link: 'https://github.com/lina-ova/blogg',
        imageSrc: img2
    },
    {
        name: 'Dragon Game (desktop app)',
        link: 'https://github.com/lina-ova/ot-harjoitystyo',
        imageSrc: img3
    }
]

const Projects=()=> {

    const ZoomInScrollOut = batch(StickyIn(), FadeIn(), ZoomIn())
  return (
    <><ScrollPage page={1}>
          <Animator animation={ZoomInScrollOut}>
              <div className="section-title">
                  Portfolio
              </div>
          </Animator>
      </ScrollPage><ScrollPage page={2}>
              <Animator animation={batch(Fade(), Sticky(), MoveOut(0, -200))}>
                  <div className="portfolio-content">
                      {work?.map((work, index) => {
                          return (
                              <a href={work?.link} target='_blank' key={work?.name}>
                                  <div className="portfolio-card">
                                      <div className="project-image">
                                          <img src={work?.imageSrc} alt={work?.name} />
                                      </div>
                                      <div className="portfolio-title">
                                          <h3>{work?.name}</h3>
                                      </div>
                                      <div className="count">
                                          {index}
                                      </div>
                                  </div>
                              </a>
                          )
                      })}
                  </div>
              </Animator>
          </ScrollPage></>
  )
}

export default Projects;
